import React, { Component } from "react";



class Application extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // Cual es el estado inicial?
      items: defaultState
    };
  }

  // Como vamos a manipular el estado?
  // Idealmente, usuarios/as van a querer agregar, remover y
  // chequear items, correcto?
  handleCheck = (id, packed) => {
    const { items = [] } = this.state;
    //console.log(id, packed);
    const newItems = items.map(item => {
      if (item.id === id) {
        return {
          ...item,
          packed
        };
      }
      return item;
    });
    this.setState({ items: newItems });
  };
  handleRemove = (id, index) => {
    console.log(index);

    const { items = [] } = this.state;
    items.splice(index, 1);
    this.setState({ items: [...items] });
  };

  handleUnPacked = (id, packed) => {
    const { items = [] } = this.state;

    items.map(item => (item.packed = false));
    console.log(items);
    this.setState({ items: items });
  };
  handleNew = value => {
    const { items = [] } = this.state;
    items.push({ value: value, id: uniqueId(), packed: false });
    console.log(value);
    this.setState({ items: [...items] });
  };
  handlefilter = item => {};

  render() {
    // obten los items del estado
    const { items } = this.state;
    //console.log(items);

    return (
      <div className="Application">
        <NewItem handleNew={this.handleNew} />
        <Items
          title="Items no empacados"
          items={items.filter(({ packed }) => packed === false)}
          handleCheck={this.handleCheck}
          handleRemove={this.handleRemove}
        />
        <Items
          title="Items empacados"
          items={items.filter(({ packed }) => packed === true)}
          handleCheck={this.handleCheck}
          handleRemove={this.handleRemove}
        />
        <button className="button full-width" onClick={this.handleUnPacked}>
          Marcar todos como desempacados
        </button>
      </div>
    );
  }
}

export default Application;
