import React, { Component } from "react";


class Item extends Component {
  render() {
    const { item, handleCheck, handleRemove, index } = this.props;
    return (
      <article className="Item">
        <label htmlFor={item.id}>
          <input
            type="checkbox"
            checked={item.packed}
            onChange={e => {
              console.log(e.target.checked);
              handleCheck(item.id, e.target.checked);
            }}
            id={item.id}
          />
          {item.value}
        </label>
        <button
          className="Item-remove"
          onClick={evento => {
            console.log(index);
            handleRemove(item.id, index);
          }}
        >
          Remove
        </button>
      </article>
    );
  }
}

export default Item;
